package flowcontrolgroups;

public class DoWhileDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//print first n natural numbers
		//input
		int number = 8;
		
		int i =1;
		do {
			//body of loop
			System.out.
			println(i);
			//expression
			i++;
		}while(i <= number);
	}

}

