
public class halfofnumber {

	// program to find half of number
	
		public static int halfofanumber(int c) {
			 int half = c/2;
			 return half;
		}
		public static void main(String[] args) {
			// TODO Auto-generated method stub
			int c = 30;
			 //method invocation
			int half = halfofanumber(c);
			//output
			
			System.out.println("Square of " + c + " is " + half);
			
		}
}
